import streamlit as st
import pandas as pd
import numpy as np

def about_dataset():
    st.write("**Tentang Dataset**")
    col1,col2 = st.columns([5,5])

    with col1:
        link = "data:image/jpeg;base64,"
        st.image(link, caption = 'Student Performance')

    with col2:
        st.write('Dataset Student Performance berisi data karakteristik siswa, '\
                 'yang mencakup aspek jam belajar, skor sebelumnya, jam tidur, performa index, '\
                 'menganalisis faktor - faktor yang memengaruhi prestasi akademik. '\
                 'Variabel utama dalam dataset ini adalah **Performance Index**, ' \
                 'yang mempresentasikan nilai akademik siswa, sementara variabel lain, '\
                 'seperti jam tidur, jam belajar, dan lain - lain digunakan untuk memahami, '\
                 'hubungan antara perilaku dan lingkungan siswa terhadap performa belajar.')
    st.write("**Deskripsi Dataset yang Digunakan**")
    st.write("- **Hours Studied** : Adalah lama waktu belajar siswa")
    st.write("- **Previous Scores** : Adalah nilai sebelumnya dari siswa")
    st.write("- **Extracurricular Activities** : Adalah status siswa apakah mengikuti kegiatan diluar kelas atau tidak ")
    st.write("- **Sleep Hours** : Adalah waktu tidur siswa")
    st.write("- **Sample Question Papers Practieced** : Adalah jumlah latihan yang dilkukan siswa")
    st.write("- **Performance Index** : Adalah nilai akhir dari siswa")